package org.raistlic.common.permutation

import org.raistlic.common.precondition.InvalidParameterException
import spock.lang.Specification
import spock.lang.Unroll

class FactorialSpec extends Specification {

    @Unroll
    void 'of() validates parameters and throws exception when number is #number'() {
        when:
        Factorial.of(number)

        then:
        thrown InvalidParameterException

        where:
        number << [-1, -2, -3, Integer.MIN_VALUE]
    }

    @Unroll
    void 'of() returns expected result #result for number #number'() {
        expect:
        Factorial.of(number) == result
        where:
        number | result
        0      | BigInteger.valueOf(1)
        1      | BigInteger.valueOf(1)
        2      | BigInteger.valueOf(2)
        3      | BigInteger.valueOf(6)
        4      | BigInteger.valueOf(24)
        100    | new BigInteger('93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000')
        1000   | new BigInteger('')
        999    | new BigInteger('')
        998    | new BigInteger('')
    }
}
